import React from 'react';
import './App.css';
import Axios from 'axios';
import { useState, useEffect } from 'react';

function AddAlbum() {
  const [formData, setFormData] = useState({
    artist: '',
    title: '',
    genre: '',
    recordLabel: '',
    releaseDate: '',
    description: '',
    rating: '',
    coverImage: ''
  });

  const handleChange = event => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value
    });
  };

  const handleSubmit = event => {
    event.preventDefault();
    // Do something with the form data, such as sending it to an API
    console.log(formData);
  };

  return (
    <>
        <section className="section has-background-link-dark">
            <div class="columns is-centered">
                <div class="column"></div>
                <div class="column is-half has-text-centered"><h1 class="title is-1 has-text-white">Albums of the Year</h1></div>
                <div class="column has-text-right">
                <Link to='addAlbum'>
                    <button class="button is-white">Add Release</button>
                </Link>
                </div>
            </div>
        </section>
        <form onSubmit={handleSubmit}>
        <label>
            Artist:
            <input
            type="text"
            name="artist"
            value={formData.artist}
            onChange={handleChange}
            />
        </label>
        <br />
        <label>
            Title:
            <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            />
        </label>
        <br />
        <label>
            Genre:
            <input
            type="text"
            name="genre"
            value={formData.genre}
            onChange={handleChange}
            />
        </label>
        <br />
        <label>
            Record Label:
            <input
            type="text"
            name="recordLabel"
            value={formData.recordLabel}
            onChange={handleChange}
            />
        </label>
        <br />
        <label>
            Release Date:
            <input
            type="date"
            name="releaseDate"
            value={formData.releaseDate}
            onChange={handleChange}
            />
        </label>
        <br />
        <label>
            Description:
            <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            />
        </label>
        <br />
        <label>
            Rating:
            <input
            type="number"
            name="rating"
            min="1"
            max="10"
            value={formData.rating}
            onChange={handleChange}
            />
        </label>
        <br />
        <label>
            Cover Image:
            <input
            type="file"
            name="coverImage"
            onChange={handleChange}
            />
        </label>
        <br />
        <button type="submit">Add Album</button>
        </form>
    </>
  );
}

export default AddAlbum
