import './App.css';
import Axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import apiBasePath from './globalVars';

function Main() {

  const personid = 3;
  
  const [albumList, setalbumList] = useState([]);
  
  const getAlbums = () => {
    Axios.get(apiBasePath + '/getalbums/' + personid)
      .then(
        res => {
          let array = []
          for (let i=0; i < length(res.data); i++) {
            array[i] = {
              index: i,
              album: res.data[i]
            }
          }
          setalbumList(array)
        }
      )
  }

  useEffect(
    () => getAlbums(),
    []
  )

  const handleChange = (event, album) => {
    event.preventDefault()

  }

  const handleSubmit = (event, albumid, rating) => {
    event.preventDefault()
    Axios.put(apiBasePath + '/updatescore/' + personid + '/' + albumid + '/' + rating)
      .then(    
        (res) => {
          console.log(res)
          getAlbums()
        }
      )
      .catch(
        (res) => console.log(res)
      )
  }
  
  const deletealbum = (id) => {
    var confirm = window.confirm('Delete album?')
    confirm ? 
      Axios.delete(apiBasePath + '/deletealbum/' + id)
        .then(
          () => getAlbums()
        )
    : void(0)
  }

  return (
    <div id = 'root'>
      <section className="section has-background-link-dark">
        <div class="columns is-centered">
          <div class="column"></div>
          <div class="column is-half has-text-centered"><h1 class="title is-1 has-text-white">Albums of the Year</h1></div>
          <div class="column has-text-right">
            <Link to='addalbum'>
              <button class="button is-white">Add album</button>
            </Link>
          </div>
        </div>
      </section>
      {
        Array.isArray(albumList) ?
          albumList.map(
            (album) => {
              return (
                <div class="block">
                  <div class="columns is-vcentered">
                    <div class="column is-3"></div>
                    <div class="column is-2">
                      <strong>{album.artist}</strong><br/>
                      <i>{album.title}</i><br/>
                      Release Date: {(album.releasedate == null) ? null : album.releasedate.substring(0,10)}<br/>
                      {album.genre}<br/>
                      {album.recordlabel}<br/>
                      Added by: {album.addedby}<br/>
                      Added date: {(album.addeddate == null) ? null : album.addeddate.substring(0,10)}<br/>
                    </div>
                    <div class="column is-2">
                      <form class="form">
                        <div class="field is-horizontal">
                          <div class="field-body">
                            <div class="field is-narrow">
                              <div class="control">
                                <input class="input" name="score" type="number" min="0" max="10" placeholder={album.rating} onChange={(e) => handleChange(e, album)}></input>
                              </div>
                            </div>
                            <div class="field">
                              <div class="control">
                                <input class="input" type="reset"></input>
                              </div>
                            </div>
                            <button onClick={(e) => handleSubmit(e, album.albumid, album.rating)}>Submit</button>
                          </div>
                        </div>
                      </form>
                    </div>
                    <div class="column is-2">
                      <article class="message">
                        <div class="message-header">
                          <p>Average score</p>
                        </div>
                        <div class="message-body">
                          {album.averagescore}
                        </div>
                      </article>
                    </div>
                    <div class="column is-3">
                      <div class="block">
                        <button class="button is-danger" onClick={() => {deletealbum(album.albumid)}}>Delete Album</button>
                        <Link to={`editalbum/${album.albumid}`}>
                          <button class="button is-green">Edit Album</button>
                        </Link>
                      </div>
                      <div class="block">
                      </div>
                    </div>
                  </div>
                </div>
              )
            }
          )
        : null
      }
    </div>
  );
}

export default Main;
