import './App.css';
import Axios from 'axios';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ReactSession } from 'react-client-session';
import apiBasePath from './globalVars';

function Login () {

  const navigate = useNavigate()
  ReactSession.setStoreType("cookie");

  const [input, setInput] = useState()

  const handleChange = event => {
    setInput(event.target.value)
  }

  const handleSubmit = event => {
    event.preventDefault()
    console.log('here')
    Axios.get(apiBasePath + '/getlogin/' + input)
      .then(
        res => console.log(res)
      )
      .catch(
        err => console.log(err)
      )
  }

  const demoMode = () => {
    ReactSession.set("personid", -1)
    navigate('/')
  }

  return(
    <>
      <section className="section has-background-link-dark">
          <div class="columns is-centered">
              <div class="column"></div>
              <div class="column is-half has-text-centered">
                  <h1 class="title is-1 has-text-white">Albums of the Year</h1>
              </div>
              <div class="column"></div>
          </div>
      </section>
      <div class="container">
        <div class="box">
          <div class="form">
            <div class="field">
              <label class="label">Enter code:</label>
              <div class="control">
                <input class="input" type="text" onChange={handleChange}></input>
              </div>
            </div>

            <div class="field is-grouped">
              <p class="control">
                <input class="button"></input>
              </p>
              <p class="control">
                <a class="button" onClick={demoMode}>Use demo mode (recruiters click here)</a>
              </p>
            </div>

          </div>
        </div>
      </div>
    </>
  )
}

export default Login
