import './App.css';
import AddAlbum from './addAlbum';
import Axios from 'axios';
import { useState, useEffect } from 'react';
import { Routes, Route, Link } from 'react-router-dom'

function App() {
  const apiBasePath = 'http://localhost:3001';
  const [releaseList, setReleaseList] = useState("");
  useEffect(
    () => {
      Axios.get(apiBasePath + '/getAlbums')
        .then(
          res => setReleaseList(res.data)
        )
    },
    []
  )

  const deleterelease = (id) => {
    var confirm = window.confirm('Delete album?')
    confirm ?
      Axios.delete(apiBasePath + '/deletealbum/' + id)
        .then(
          () => {
            Axios.get(apiBasePath + '/getAlbums')
              .then(
                res => setReleaseList(res.data)
              )
          }
        )
    : void(0)
  }

  return (
    <>
      <Routes>
        <Route path='/addAlbum' element={ <AddAlbum/> } />
      </Routes>
      <div id = 'root'>
        <section className="section has-background-link-dark">
          <div class="columns is-centered">
            <div class="column"></div>
            <div class="column is-half has-text-centered"><h1 class="title is-1 has-text-white">Albums of the Year</h1></div>
            <div class="column has-text-right">
              <Link to='addAlbum'>
                <button class="button is-white">Add Release</button>
              </Link>
            </div>
          </div>
        </section>
        {
          Array.isArray(releaseList) ?
            releaseList.map(
              (release) => {
                return (
                  <div class="block">
                    <div class="columns is-vcentered">
                      <div class="column is-3"></div>
                      <div class="column is-3">
                        <strong>{release.artist}</strong><br/>
                        <i>{release.title}</i><br/>
                        Released: {release.releasedate.substring(0,10)}<br/>
                        {release.genre}<br/>
                        {release.recordlabel}<br/>
                        Added by: {release.addedby}<br/>
                        Added date: {release.addeddate.substring(0,10)}<br/>
                        {release.id}
                      </div>
                      <div class="column is-3"></div>
                      <div class="column is-3">
                        <div class="block">
                          <a class="button is-danger" onClick={() => {deleterelease(release.albumid)}}>Delete Release</a>
                        </div>
                        <div class="block">
                        </div>
                      </div>
                    </div>
                  </div>
                )
              }
            )
          : null
        }
      </div>
    </>
  );
}

export default App;
