import './App.css';
import Axios from 'axios';
import { useState, useEffect } from 'react';

function App() {
  const apiBasePath = 'http://localhost:3000';
  const [releaseList, setReleaseList] = useState("");
  useEffect(
    () => {
      Axios.get('http://localhost:3001/getAlbums')
        .then(
          (res) => {
            setReleaseList(res.data)
          }
        )
    },
    []
  )

  return (
    <div id = 'root'>
      <section className="section has-background-link-dark">
        <div class="columns is-centered">
          <div class="column"></div>
          <div class="column is-half has-text-centered"><h1 class="title is-1 has-text-white">Albums of the Year</h1></div>
          <div class="column has-text-right"><button class="button is-white">Add Release</button></div>
        </div>
      </section>
      <div>
        {
          releaseList.map(
            (release) => {
              return (
                <div class="block">
                  <div class="columns">
                    <div class="column is 3"></div>
                    <div class="column is 3">
                      <strong>{release.artist}</strong><br/>
                      <i>{release.title}</i><br/>
                      Released: {substr(release.date,0,10)}<br/>
                      {release.genre}<br/>
                      {release.recordlabel}<br/>
                      Added by: {release.addedby}<br/>
                      Added date: {release.releasedate}<br/>
                    </div>
                    <div class="column is 3"></div>
                    <div class="column is 3"></div>
                  </div>
                </div>
              )
            }
          )
        }
      </div>
    </div>
  );
}

export default App;
