import './App.css';
import Axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import apiBasePath from './globalVars';

function Main() {

  const personid = 3;
  
  const [albumList, setalbumList] = useState([]);
  
  const getAlbums = () => {
    Axios.get(apiBasePath + '/getalbums/' + personid)
      .then(
        res => {
          let array = []
          for (let i=0; i < res.data.length; i++) {
            array[i] = {
              index: i,
              album: res.data[i]
            }
          }
          setalbumList(array)
          /*for (let i = 0; i < document.getElementsByName('form').length; i++) {
            document.getElementsByName('form')[i].reset()
          }
          for (let i = 0; i < document.getElementsByName('scoreNum').length; i++) {
            if (document.getElementsByName('scoreNum')[i].hasAttribute('value')) {
              document.getElementsByName('scoreNum')[i].removeAttribute('value')
            }
          }*/
        }
      )
  }

  useEffect(
    () => getAlbums(),
    []
  )

  const handleChange = (event, item) => {
    let albums = [...albumList]
    albums[item].album.rating = event.target.value
    document.getElementById('scoreNum' + item).setAttribute("value", albums[item].album.rating)
    document.getElementById('scoreRange' + item).setAttribute("value", albums[item].album.rating)
  }

  const handleSubmit = (event, albumid, rating) => {
    event.preventDefault()
    if (rating < 0 || rating > 10) {
      window.alert('Ratings must be between 0 and 10')
    }
    else {
      Axios.put(apiBasePath + '/updatescore/' + personid + '/' + albumid + '/' + rating)
        .then(    
          (res) => {
            getAlbums()
          }
        )
    }
  }

  const onClear = (item) => {
    document.getElementById('scoreNum' + item).removeAttribute("value")
    document.getElementById('scoreRange' + item).setAttribute("value", document.getElementById('scoreRange' + item).getAttribute('defaultValue').value)
  }
  
  const deletealbum = (id) => {
    var confirm = window.confirm('Delete album?')
    confirm ? 
      Axios.delete(apiBasePath + '/deletealbum/' + id)
        .then(
          () => {
            getAlbums()
            onClear(id)
          }
        )
    : void(0)
  }

  return (
    <div id = 'root'>
      <section className="section has-background-link-dark">
        <div class="columns is-centered">
          <div class="column"></div>
          <div class="column is-half has-text-centered"><h1 class="title is-1 has-text-white">Albums of the Year</h1></div>
          <div class="column has-text-right">
            <Link to='addalbum'>
              <button class="button is-white">Add album</button>
            </Link>
          </div>
        </div>
      </section>
      {
        Array.isArray(albumList) ?
          albumList.map(
            (listItem) => {
              return (
                <div class="block">
                  <div class="columns is-vcentered">
                    <div class="column is-3"></div>
                    <div class="column is-2">
                      <strong>{listItem.album.artist}</strong><br/>
                      <i>{listItem.album.title}</i><br/>
                      Release Date: {(listItem.album.releasedate == null) ? null : listItem.album.releasedate.substring(0,10)}<br/>
                      {listItem.album.genre}<br/>
                      {listItem.album.recordlabel}<br/>
                      Added by: {listItem.album.addedby}<br/>
                      Added date: {(listItem.album.addeddate == null) ? null : listItem.album.addeddate.substring(0,10)}<br/>
                    </div>
                    <div class="column is-2">
                      <form class="form" name='form' id={'form' + listItem.index}>
                        <div class="field">
                          <label class="label">Your rating:</label>
                          <div class="control">
                            <input class="input" name='scoreRange' id={'scoreRange' + listItem.index} type="range" min="0" max="10" step="0.1" defaultValue={listItem.album.rating} onChange={(e) => handleChange(e, listItem.index)}></input>
                          </div>
                        </div>
                        <div class="field is-horizontal">
                          <div class="field-body">
                            <div class="field is-narrow">
                              <div class="control">
                                <input class="input" name='scoreNum' id={'scoreNum' + listItem.index} type="number" min="0" max="10" placeholder={listItem.album.rating} onChange={(e) => handleChange(e, listItem.index)}></input>
                              </div>
                            </div>
                            <div class="field">
                              <div class="control">
                                <input class="input" type="reset" onClick={() => onClear(listItem.index)}></input>
                              </div>
                            </div>
                            <div class="field">
                              <div class="control">
                                <button class="button is-dark" onClick={(e) => handleSubmit(e, listItem.album.albumid, listItem.album.rating)}>Submit</button>
                              </div>
                            </div>
                          </div>
                        </div>
                      </form>
                    </div>
                    <div class="column is-2">
                      <article class="message">
                        <div class="message-header">
                          <p>Average score</p>
                        </div>
                        <div class="message-body">
                          {listItem.album.averagescore}
                        </div>
                      </article>
                    </div>
                    <div class="column is-3">
                      <div class="block">
                        <button class="button is-danger" onClick={() => {deletealbum(listItem.album.albumid)}}>Delete Album</button>
                        <Link to={`editalbum/${listItem.album.albumid}`}>
                          <button class="button is-green">Edit Album</button>
                        </Link>
                      </div>
                      <div class="block">
                      </div>
                    </div>
                  </div>
                </div>
              )
            }
          )
        : null
      }
    </div>
  );
}

export default Main;
