import './App.css';
import Axios from 'axios';
import { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import apiBasePath from './globalVars';
import { ReactSession } from 'react-client-session';
import ColorThief from 'colorthief'

function Main() {
  const personid = ReactSession.get("personid");
  const [albumsList, setAlbumsList] = useState([])
  const getAlbums = () => {
    Axios.get(apiBasePath + '/getalbums/' + personid)
      .then(
        res => {
          let albums = []
          for (let i = 0; i < res.data.length; i++) {
            albums[i] = {
              index: i,
              album: res.data[i]
            }
          }
          setAlbumsList(albums)
        }
      )
  }
  useEffect(
    () => getAlbums(),
    []
  )
  const Album = ({ album, index, ratings, handleChange, handleReset }) => {
    const inputRef = useRef();
    const coverRef = useRef();
    const blockRef = useRef();
    const [color0, setColor0] = useState();
    const [color1, setColor1] = useState();
    const [color2, setColor2] = useState();
    const [hoverState, setShadowState] = useState();
    const setDisabledInputs = (addedbypersonid) => {
      if (personid === -1 && addedbypersonid !== -1) {
        return true
      }
      else {
        return false
      }
    }
    const deletealbum = (id) => {
      var confirm = window.confirm('Delete album?')
      confirm ? 
        Axios.delete(apiBasePath + '/deletealbum/' + id)
          .then(
            () => {
              getAlbums()
            }
          )
      : void(0)
    }
    const handleSubmit = (event, albumid, rating) => {
      if (rating < 0 || rating > 10) {
        window.alert('Ratings must be between 0 and 10')
      }
      else if (rating === "") {
        void(0)
      }
      else {
        Axios.put(apiBasePath + '/updatescore/' + personid + '/' + albumid + '/' + rating)
          .then(
            () => {
              getAlbums()
              
            }
          )
      }
    }
    const getColors = (coverRef) => {
      const img = coverRef.current
      const colorThief = new ColorThief()
      const result = colorThief.getPalette(img, 3)
      setColor0('rgb('+result[0][0]+','+result[0][1]+','+result[0][2]+')')
      setColor1('rgb('+result[1][0]+','+result[1][1]+','+result[1][2]+')')
      setColor2('rgb('+result[2][0]+','+result[2][1]+','+result[2][2]+')')
    }
    const onHover = blockRef => {
      setShadowState(`5px 5px 5px ${color2}`)
    }
    const onLeave = blockRef => {
      setShadowState("")
    }
    return (
      <div
        class="block"
        ref={blockRef}
        key={album.index}
        onMouseEnter={() => onHover(blockRef)}
        onMouseLeave={() => onLeave(blockRef)}
        style={{margin: "2%", backgroundColor: color0, boxShadow: hoverState, transition: "0.5s"}}
      >
        <div class="columns is-vcentered">
          <div class="column is-3 has-text-centered">
            <img
              src="/covers/Get_to_Heaven_Everything_Everything (1).jpg"
              alt="cover"
              ref={coverRef}
              onLoad={() => getColors(coverRef)}
            ></img>
          </div>
          <div class="column is-2" style={{color: color1}}>
            <strong style={{color: color1}}>{album.album.artist}</strong><br />
            <i style={{color: color1}}>{album.album.title}</i><br />
            Release Date: {(album.album.releasedate == null) ? null : album.album.releasedate.substring(0, 10)}<br />
            {album.album.genre}<br />
            {album.album.recordlabel}<br />
            Added by: {album.album.addedbypersonname}<br />
            Added date: {(album.album.addeddate == null) ? null : album.album.addeddate.substring(0, 10)}<br />
          </div>
          <div class="column is-2">
            <form class="form" name='form'>
              <div class="field">
                <label class="label" style={{color: color1}}>Your rating:</label>
                <div class="control">
                  <input
                    class="input"
                    name='scoreRange'
                    type="range"
                    min="0"
                    max="10"
                    step="0.1"
                    defaultValue={album.album.rating}
                    value={ratings[index]}
                    onChange={(e) => handleChange(index, e.target.value)}
                    disabled={setDisabledInputs(album.album.addedbypersonid)}
                    ref={inputRef}
                  />
                </div>
              </div>
              <div class="field is-horizontal">
                <div class="field-body">
                  <div class="field is-narrow">
                    <div class="control">
                      <input
                        class="input"
                        name='scoreNum'
                        type="number"
                        min="0"
                        max="10"
                        step="0.1"
                        placeholder={album.album.rating}
                        value={album.album.rating === ratings[index] ? null : ratings[index]}
                        onChange={(e) => handleChange(index, e.target.value)}
                        disabled={setDisabledInputs(album.album.addedbypersonid)}
                      />
                    </div>
                  </div>
                  <div class="field">
                    <div class="control">
                      <input
                        class="input"
                        type="reset"
                        onClick={() => handleReset(index, inputRef)}
                        disabled={setDisabledInputs(album.album.addedbypersonid)}
                      />
                    </div>
                  </div>
                  <div class="field">
                    <div class="control">
                      <button
                        type="button"
                        class="button is-dark"
                        disabled={setDisabledInputs(album.album.addedbypersonid)}
                        onClick={e => handleSubmit(e, album.album.albumid, ratings[index])}
                        style={{backgroundColor: color1, color: color0}}
                      >
                        Submit
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </form>
          </div>
          <div class="column is-2">
            <article class="message">
              <div class="message-header" style={{backgroundColor: color1, color: color0}}>
                <p>Average score</p>
              </div>
              <div class="message-body">
                {album.album.averagescore}
              </div>
            </article>
          </div>
          <div class="column is-3">
            <div class="block">
              <button
                class="button is-danger"
                onClick={() => { deletealbum(album.album.albumid) }}
                disabled={setDisabledInputs(album.album.addedbypersonid)}
              >
                Delete Album
              </button>
              <Link to={`editalbum/${album.album.albumid}`}>
                <button
                  class="button is-green"
                  disabled={setDisabledInputs(album.album.addedbypersonid)}
                >
                  Edit Album
                </button>
              </Link>
            </div>
            <div class="block">
            </div>
          </div>
        </div>
      </div>
    )
  }

  const AlbumList = ({ albums }) => {
    const [ratings, setRatings] = useState(albums.map(album => album.album.rating))
    const handleChange = (index, newRating) => {
      const newRatings = [...ratings]
      newRatings[index] = newRating
      setRatings(newRatings)
    }
    const handleReset = (index, inputRef) => {
      setRatings(
        ratings => {
          const newRatings = [...ratings]
          newRatings[index] = albums[index].album.rating
          inputRef.current.value = null
          return newRatings
        }
      )
    }
    return (
      <>
        {
          albums.map(
            (album, index) => (
              <Album
                key={album.index}
                album={album}
                index={index}
                ratings={ratings}
                handleChange={handleChange}
                handleReset={handleReset}
              />
            )
          )
        }
      </>
    )
  }
  return (
    <>
      <section className="section has-background-link-dark">
        <div class="columns is-centered">
          <div class="column"></div>
          <div class="column is-half has-text-centered"><h1 class="title is-1 has-text-white">Albums of the Year</h1></div>
          <div class="column has-text-right">
            <Link to='addalbum'>
              <button class="button is-white">Add album</button>
            </Link>
          </div>
        </div>
      </section>
      <AlbumList albums={albumsList}/>
    </>
  )
}

export default Main;
